import React, { Component } from 'react';
import './App.css';

class App extends Component {

constructor() {
  super();

  this.state = {
      numeroDeCliques: 0
    }

  this.handleClick1 = this.handleClick1.bind(this);
  this.handleClick2 = this.handleClick2.bind(this);
  this.handleClick3 = this.handleClick3.bind(this);
}

handleClick1() {
  console.log('Clicou no Botão 1!');
  this.setState({
      numeroDeCliques1: 1
    })
}

handleClick2() {
  console.log('Clicou no Botão 2!');
    this.setState({
      numeroDeCliques2: 1
    })
}

handleClick3() {
  console.log('Clicou no Botão 3!');
  this.setState(() => ({
    numeroDeCliques3: 1,
  }));
    this.setState(({ clicksBtnThree }) => ({
    clicksBtnThree: clicksBtnThree + 1,
  }));
}

  render() {
    return (
    <>
      <button onClick={this.handleClick1}>Botão 1 - {this.state.numeroDeCliques1} Cliques</button>
      <button onClick={this.handleClick2}>Botão 2- {this.state.numeroDeCliques2} Cliques</button>
      <button onClick={this.handleClick3}>Botão 3 - {this.state.numeroDeCliques3} Cliques</button>   
    </>
    )
  }
}

export default App;
